CREATE TABLE donations (id BIGINT AUTO_INCREMENT, processed VARCHAR(255), donationhash VARCHAR(255), email VARCHAR(255), payerid VARCHAR(255), payerstatus VARCHAR(255), firstname VARCHAR(255), lastname VARCHAR(255), countrycode VARCHAR(255), shiptoname VARCHAR(255), shiptostreet VARCHAR(255), shiptostreet2 VARCHAR(255), shiptocity VARCHAR(255), shiptostate VARCHAR(255), shiptozip VARCHAR(255), shiptophonenum VARCHAR(255), shiptocountrycode VARCHAR(255), shiptocountryname VARCHAR(255), sellernote VARCHAR(255), donationmessage text, anonymous TINYINT(1), addressstatus VARCHAR(255), quantity BIGINT, shipping DECIMAL(4, 2), eggprice DECIMAL(4, 2), total DECIMAL(4, 2), status VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
